mapper.select:
SELECT SEQ, ID, TITLE, CONTENT, READCOUNT, WDATE, DEL FROM (SELECT ROW_NUMBER() OVER(ORDER BY SEQ DESC)AS RNUM, SEQ, ID, TITLE, CONTENT, READCOUNT, WDATE, DEL FROM SH_NBBS WHERE 1=1
AND TITLE LIKE '%'||#{s_keyword}||'%' AND DEL=0
AND CONTENT LIKE '%'||#{s_keyword}||'%' AND DEL=0
) A WHERE RNUM BETWEEN ${start} AND ${end}

mapper.update:
UPDATE SH_NBBS
SET READCOUNT=READCOUNT+1
WHERE SEQ=#{seq}

UPDATE SH_NBBS
SET TITLE=#{title}, CONTENT=#{content}
WHERE SEQ=#{seq}

UPDATE SH_NBBS
SET DEL=1
WHERE SEQ=#{seq}
