mapper.select:
SELECT COUNT(*)		
FROM SH_VOTER
WHERE POLLID=#{pollid} AND ID=#{id}

SELECT POLLSUBID, POLLID, ANSWER, ACOUNT
FROM SH_POLLSUB
WHERE POLLID=#{pollid}

mapper.update:
UPDATE SH_POLL
SET POLLTOTAL=POLLTOTAL+1
WHERE POLLID=#{pollid}

UPDATE SH_POLLSUB
SET ACOUNT=ACOUNT+1
WHERE POLLSUBID=#{pollsubid} AND POLLID=#{pollid}
