mapper.select:
SELECT SEQ, ID, TITLE, CONTENT, READCOUNT, WDATE, PARENT, REF, STEP, DEPTH, REGDATE, DEL FROM (SELECT ROW_NUMBER() OVER(ORDER BY REF DESC, STEP ASC)AS RNUM, SEQ, ID, TITLE, CONTENT, READCOUNT, WDATE, PARENT, REF, STEP, DEPTH, REGDATE, DEL FROM SH_QBBS WHERE 1=1
AND TITLE LIKE '%'||#{q_keyword}||'%'
AND CONTENT LIKE '%'||#{q_keyword}||'%'
AND ID LIKE '%'||#{q_keyword}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end}

mapper.update:
UPDATE SH_QBBS SET 
TITLE=#{title}, CONTENT=#{content}
WHERE SEQ=#{seq}

UPDATE SH_QBBS
SET DEL=1
WHERE SEQ=#{seq}

UPDATE SH_QBBS SET 
READCOUNT=READCOUNT+1
WHERE SEQ=#{seq}
